mapper.select:
/* co0102.retrieveCO0101MList */ SELECT A.SPEC_NO , A.SPEC_NM , A.EQ_TP , B.CLS_NM EQ_TP_NM , A.FST_REG_DT , A.FST_REG_USER_ID , A.FNL_EDIT_DT , A.FNL_EDIT_USER_ID FROM CO0101M A LEFT OUTER JOIN EQ0201M B ON (B.CLS_CD = A.EQ_TP AND REV_NO = #{revNo}) WHERE 1=1
AND UPPER(A.EQ_TP) LIKE '%' + UPPER(#{eqTpS}) + '%'
AND UPPER(A.SPEC_NM) LIKE '%' + UPPER(#{specNmS}) + '%'
AND A.SPEC_DIV = #{specDivS}
ORDER BY SPEC_NO

mapper.insert:
 /* co0102.insertCO0101M */ 
INSERT INTO CO0101M (
       SPEC_DIV
     , SPEC_NO
     , SPEC_NM
     , EQ_TP
     , FST_REG_DT
     , FST_REG_USER_ID
     , FNL_EDIT_DT
     , FNL_EDIT_USER_ID
 )
 VALUES (
       #{specDiv}
     , #{specNo}
     , #{specNm}
     , #{eqTp}
     , GETDATE()
     , #{fstRegUserId}
     , GETDATE()
     , #{fnlEditUserId}
)

mapper.update:
 /* co0102.updateCO0101M */ 
UPDATE CO0101M
   SET
       SPEC_DIV         = #{specDiv}
     , SPEC_NM          = #{specNm}
     , EQ_TP            = #{eqTp}
     , FNL_EDIT_DT      = GETDATE()
     , FNL_EDIT_USER_ID = #{fnlEditUserId}
 WHERE SPEC_NO = #{specNo}

mapper.delete:
 /* co0102.deleteCO0101M */ 
DELETE
  FROM CO0101M
    WHERE SPEC_NO = #{specNo}

mapper.select:
/* co0102.retrieveCO0101DList */ SELECT SPEC_NO , SPEC_DTL_NO , SEQ_NO , SPEC_DESC , UNIT_CD , UNIT_NM , ARRAY_ORD , FST_REG_DT , FST_REG_USER_ID , FNL_EDIT_DT , FNL_EDIT_USER_ID FROM CO0101D WHERE SPEC_NO = #{specNo}
AND UPPER(SPEC_DESC) LIKE '%' + UPPER(#{specDescS}) + '%'
ORDER BY ARRAY_ORD

mapper.insert:
 /* co0102.insertCO0101D */ 
INSERT INTO CO0101D (
       SPEC_NO
     , SPEC_DTL_NO
     , SEQ_NO
     , SPEC_DESC
     , UNIT_CD
     , UNIT_NM
     , ARRAY_ORD
     , FST_REG_DT
     , FST_REG_USER_ID
     , FNL_EDIT_DT
     , FNL_EDIT_USER_ID
)
VALUES (
       #{specNo}
     , #{specDtlNo}
     , (SELECT ISNULL(MAX(SEQ_NO) + 1,0) FROM CO0101D WHERE SPEC_NO = #{specNo})
     , #{specDesc}
     , #{unitCd}
     , #{unitNm}
     , #{arrayOrd}
     , GETDATE()
     , #{fstRegUserId}
     , GETDATE()
     , #{fnlEditUserId}
)

mapper.update:
 /* co0102.updateCO0101D */ 
UPDATE CO0101D
   SET
       SPEC_DESC        = #{specDesc}
     , UNIT_CD          = #{unitCd}
     , UNIT_NM          = #{unitNm}
     , ARRAY_ORD        = #{arrayOrd}
     , FNL_EDIT_DT      = GETDATE()
     , FNL_EDIT_USER_ID = #{fnlEditUserId}
 WHERE SPEC_NO          = #{specNo}
   AND SPEC_DTL_NO      = #{specDtlNo}
   AND SEQ_NO           = #{seqNo}

mapper.delete:
 /* co0102.deleteCO0101D */ 
DELETE
  FROM CO0101D
 WHERE SPEC_NO     = #{specNo}
   AND SPEC_DTL_NO = #{specDtlNo}
   AND SEQ_NO      = #{seqNo}

 /* co0102.deleteCO0101MByCO0101D */ 
DELETE
  FROM CO0101D
 WHERE SPEC_NO   = #{specNo}

mapper.select:
/* co0102.retrieveStdSpecList */ SELECT A.SPEC_NO , A.SPEC_NM , A.EQ_TP AS EQ_TP , B.CLS_NM + ' [' + A.EQ_TP + ']' AS EQ_TP_NM FROM CO0101M A LEFT OUTER JOIN EQ0201M B ON (B.CLS_CD = A.EQ_TP AND REV_NO = #{revNo}) WHERE A.SPEC_DIV = #{specDivS}
AND UPPER(A.EQ_TP) LIKE '%' + UPPER(#{feqTp}) + '%'
AND UPPER(A.SPEC_NM) LIKE '%' + UPPER(#{fspecNo}) + '%'
AND UPPER(A.SPEC_NM) LIKE '%' + UPPER(#{fspecNm}) + '%'
ORDER BY SPEC_NO
